#include <iostream>
#include <string>
#include <cmark.h>

std::string convertMarkdownToHTML(const std::string& markdown) {
    // Convert markdown to HTML using cmark
    std::string html;
    cmark_parser *parser = cmark_parser_new();
    cmark_parser_feed(parser, markdown.c_str(), markdown.length());
    cmark_parser_finish(parser);

    // Get the output
    cmark_buffer *buffer = cmark_parser_output(parser);
    html = buffer->data;

    // Clean up
    cmark_buffer_free(buffer);
    cmark_parser_free(parser);

    return html;
}

int main() {
    std::string markdown = "# Hello World\nThis is a **markdown** text.";
    std::string html = convertMarkdownToHTML(markdown);
    std::cout << "HTML: " << html << std::endl;
    return 0;
}
```

