# Install Cypress
RUN npm install -g cypress@${CYPRESS_VERSION} && \
    cypress install

# Copy your project's package.json and package-lock.json
COPY package*.json ./

# Install project dependencies
RUN npm install

# Copy the rest of your project
COPY . .

# Expose Cypress default port
