  "defaultCommandTimeout": 10000,  // Adjust timeout as necessary
  "env": {
    "BASE_URL": "http://localhost:3000"  // Your app's base URL
  },
  "chromeWebSecurity": false,  // Disable if your app uses CORS (Cypress default)
  "screenshotsOnFail": true,
  "screenshotsOnPass": false  // Adjust based on your needs
}
