    clear();
    // Draw level components (pseudo-images)
    for (int y = 0; y < LINES; y++) {
        for (int x = 0; x < COLS; x++) {
            if (is_wall(x, y)) mvaddch(y, x, '@');
            else if (is_border(x, y)) mvaddch(y, x, '#');
            else mvaddch(y, x, ' '); // Background or empty space
