    int new_x = snake->head.x;
    int new_y = snake->head.y;

    switch (snake->direction) {
        case DIRECTION_UP: new_y -= snake->speed; break;
        case DIRECTION_DOWN: new_y += snake->speed; break;
        case DIRECTION_LEFT: new_x -= snake->speed; break;
