   const { Routes } = require('discord-api-types/v9'); // (Adjust for v14 (default checked here, specific if needed): (base is implicit))

   // Bot configuration
   const client = new Client({ intents: [Intents.FLAGS.GUILDS, Intents.FLAGS.GUILD_MESSAGES] });
   const token = 'YOUR_BOT_TOKEN';
   const clientId = 'YOUR_BOT_CLIENT_ID';
   const guildId = 'YOUR_GUILD_ID'; // (Optional if command registration is dynamic, here for clarity)

   // Command data (structure for ban command)
   const commands = [
       {
           name: 'ban',
           description: 'Bans a user from the server',
           options: [
               {
                   name: 'user',
                   description: 'The user to ban',
                   type: 'USER',
                   required: true,
               },
               {
                   name: 'days',
                   description: 'Number of days to ban (optional)',
                   type: 'INTEGER',
                   required: false,
               },
           ],
           defaultPermission: false, // Only bot admins or specific roles can use this command (adjust permissions as needed)
       },
   ];

   // Helper function to register commands (run once when bot starts)
   async function registerCommands(client, token, commands, guildId) {
       const rest = new REST({ version: '9' }).setToken(token); // Adjust version for v14 (default '9' for compatibility (v14 implicit in context))
       try {
           await rest.put(
               Routes.applicationCommands(clientId),
               { body: commands },
           );
           console.log('Successfully registered application commands.');
       } catch (error) {
           console.error(error);
       }
   }

   // Command handler (execute commands)
   client.commands = new Collection();
   commands.forEach((command) => {
       client.commands.set(command.name, command);
   });

   // Ban command implementation (using command handler pattern)
   const banCommand = {
       name: 'ban',
       description: 'Bans a user from the server',
       execute: async (interaction) => {
           const user = interaction.options.getUser('user');
           const days = interaction.options.getInteger('days', false);

           if (!user) {
               return interaction.reply({ content: 'No user provided!', ephemeral: true });
           }

           const member = interaction.guild.members.cache.get(user.id);
           if (!member) {
               return interaction.reply({ content: 'User not found!', ephemeral: true });
