#include <iostream>
#include <string>
#include <regex>

using namespace std;

string markdownToHtml(string markdownText) {
  // Regular expression to match markdown tags
  regex tag_regex("(#?[a-z0-9]+)[ \t]*(.*)");

  // Iterate over the text and match tags
  string htmlText;
  for (string tag : regex_matchall(markdownText, tag_regex)) {
    // Extract the tag content
    string content = tag.match(tag_regex)[2];

    // Replace the tag with the corresponding HTML tag
    htmlText += "<" + tag.str() + ">" + content + "</" + tag.str() + ">";
  }

  // Return the final HTML text
  return htmlText;
}

int main() {
  // Example markdown text
  string markdownText = "# Introduction\n\nThis is a markdown text.\n\n# Heading\n\nThis is a heading.";

  // Convert markdown to HTML
  string htmlText = markdownToHtml(markdownText);

  // Print the HTML text
  cout << htmlText << endl;

  return 0;
}
```

