// Comparable interface
interface Comparable<T> {
  int compare(T a, T b);
}

// Comparer for sorting in ascending order
class AscendingComparator implements Comparable<Comparable<Integer>> {
  @Override
  public int compare(Comparable<Integer> a, Comparable<Integer> b) {
    return a.compareTo(b);
  }
}

// Comparer for sorting in descending order
class DescendingComparator implements Comparable<Comparable<Integer>> {
  @Override
  public int compare(Comparable<Integer> a, Comparable<Integer> b) {
    return b.compareTo(a);
  }
}

// Recursive sorting function
def sort(nums: List<Comparable<Integer>>, comparator: Comparator<Comparable<Integer>>): Unit {
  if (nums.length <= 1) {
    return;
  }

  // Choose the appropriate comparator
  val comparatorInstance = comparator instanceof AscendingComparator ?
    AscendingComparator.getInstance() : DescendingComparator.getInstance();

  // Divide the list into two halves
  val mid = nums.length / 2;
  val left = nums.subList(0, mid);
  val right = nums.subList(mid, nums.length);

  // Recursively sort the two halves
  sort(left, comparatorInstance);
  sort(right, comparatorInstance);

  // Merge the sorted halves
  nums.replaceAll(left, right);
}

// Example usage
