const { Client, GatewayIntentBits } = require('discord.js');
const { token } = require('./config.json'); // Ensure your token is kept secure and not hardcoded in production

const client = new Client({ intents: [GatewayIntentBits.Guilds, GatewayIntentBits.GuildMessages, GatewayIntentBits.MessageContent] });

client.once('ready', () => {
  console.log(`Logged in as ${client.user.tag}!`);
});

client.on('interactionCreate', async interaction => {
  if (!interaction.isCommand()) return;

  const { commandName } = interaction;

  switch (commandName) {
    case 'ban':
      await handleBanCommand(interaction);
      break;
    default:
      await interaction.reply('This command does not exist.');
  }
});

const handleBanCommand = async (interaction) => {
  const { guildId, options } = interaction;
  const member = options.get('member') || interaction.guild.members.me;

  if (!member) {
    await interaction.reply('Please specify a member to ban.');
    return;
  }

  try {
    await member.ban({ reason: 'Bot-generated ban' });
    await interaction.reply(`Successfully banned ${member} for 'Bot-generated ban' reason.`);
  } catch (error) {
    await interaction.reply('Failed to ban the member.');
    console.error(error);
  }
};

client.login(token);
```

