```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <unordered_map>

// Function to count occurrences of a specific word in a file
int countWordOccurrences(const std::string& filename, const std::string& wordToCount) {
    std::unordered_map<std::string, int> wordCount;

    // Open the file in read mode
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Error: Could not open the file." << std::endl;
        return -1;
    }

    // Read the file line by line
    std::string line;
    while (std::getline(file, line)) {
        // Remove any leading/trailing whitespace characters
        line.erase(std::remove(line.begin(), line.end(), ' '), line.end());
        line.erase(std::remove(line.begin(), line.end(), '\n'), line.end());

        // Check if the line contains the word to count
        if (line.find(wordToCount) != std::string::npos) {
            ++wordCount[wordToCount];
        }
    }

    // Close the file
    file.close();

    // Return the total count of the word
    return wordCount[wordToCount];
}

int main() {
    std::string filename, wordToCount;

    // Get the filename and word to count from the user
    std::cout << "Enter the filename: ";
    std::getline(std::cin, filename);

    std::cout << "Enter the word to count: ";
    std::getline(std::cin, wordToCount);

    // Count the occurrences of the word in the file
    int count = countWordOccurrences(filename, wordToCount);

    // Display the result
    if (count != -1) {
        std::cout << "The word \"" << wordToCount << "\" occurs " << count << " times in the file." << std::endl;
    }

    return 0;
}
