const Discord = require('discord.js');
const client = new Discord.Client();

client.on('ready', () => {
  console.log(`Logged in as ${client.user.tag}!`);
});

client.on('message', message => {
  if (message.content.startsWith('!ban')) {
    if (!message.member.hasPermission('BAN_MEMBERS')) {
      return message.channel.send('You do not have the permission to use this command.');
    }
    let user = message.mentions.users.first();
    if (user) {
      let reason = message.content.split(' ').slice(2).join(' ');
      if (!reason) {
        return message.channel.send('Please provide a reason for the ban.');
      }
      client.guilds.cache.get('guild_id').members.ban(user, reason);
      message.channel.send(`User ${user.tag} has been banned.`);
    } else {
      message.channel.send('Please mention a user to ban.');
    }
  }
});

client.login('your_bot_token');

