```cpp
#include <iostream>
#include <string>
#include <markdowncpp/markdown.h>
#include <html/html.h>

std::string markdownToHtml(const std::string& markdown) {
    markdown::Markdown markdownParser;
    markdown::Document doc = markdownParser.parse(markdown);
    html::HTMLDocument htmlDoc = html::HTMLDocument::create(doc);
    std::string html = html::toHtml(htmlDoc);
    return html;
}

int main() {
    std::string markdown = "## Heading\n\
    This is a markdown formatted text.\n\
    It will be converted to HTML.\n";
    std::string html = markdownToHtml(markdown);
    std::cout << html << std::endl;
    return 0;
}
