class Queue:
    def __init__(self):
        self.stack1 = []
        self.stack2 = []

    def enqueue(self, data):
        while self.stack1:
            self.stack2.append(self.stack1.pop())
        self.stack1.append(data)
        while self.stack2:
            self.stack1.append(self.stack2.pop())

    def dequeue(self):
