public class PowerOfThree {
    public boolean isPowerOfThree(int n) {
        if (n == 0) {
            return false;
        }

        // Find the highest power of 3 that is less than or equal to n
        int highestPower = 1;
        while (highestPower <= n) {
            highestPower *= 3;
        }

        // If the highest power of 3 is equal to n, then n is a power of 3
        return highestPower == n;
    }

