// Function to count occurrences of a word in a file
int countWordOccurrences(const std::string& filename, const std::string& word) {
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Error opening file: " << filename << std::endl;
        return -1;
    }

    std::string line;
    int count = 0;
    std::string currentWord;

    while (std::getline(file, line)) {
        size_t pos = 0;
        while ((pos = line.find(word, pos)) != std::string::npos) {
            if (pos == 0 || std::isspace(line[pos - 1])) {
                currentWord = word;
                count++;
            } else {
                currentWord = line.substr(pos, word.length());
            }
            pos += word.length();
        }
    }

    file.close();
    return count;
}

int main() {
    std::string filename;
    std::string word;
    int occurrences;

    std::cout << "Enter the filename: ";
    std::cin >> filename;
    std::cout << "Enter the word to count: ";
    std::cin >> word;

    occurrences = countWordOccurrences(filename, word);

    if (occurrences != -1) {
        std::cout << "The word '" << word << "' occurred " << occurrences << " times in the file." << std::endl;
