#include <iostream>
#include <string>
#include <cmark.h>

std::string markdown_to_html(const std::string& markdown) {
    // Convert the input string to a cmark_input object
    cmark_input input;
    input.data = markdown.c_str();
    input.size = markdown.length();
    input.options = 0; // No specific options for this example

    // Initialize the parser
    cmark_parser *parser = cmark_parser_new();
    cmark_parser_feed(parser, &input);

    // Convert the parsed Markdown to HTML
    std::string html_output;
    cmark_parser_render_html(parser, &html_output, nullptr, 0);

    // Clean up
    cmark_parser_free(parser);

    return html_output;
}

int main() {
    std::string markdown = "# Hello World\nThis is a **Markdown** text.";
    std::string html = markdown_to_html(markdown);
    std::cout << "HTML: " << html << std::endl;
    return 0;
}
```

### Explanation:

