    # Handle mouse look
    var mouse_input = Input.get_vector("ui_right", "ui_left", 0, 0)
    mouse_input = Vector2(mouse_input.x, -mouse_input.y)  # Invert Y for typical FPS view
    camera.rotate_x(deg2rad(-mouse_input.y * mouse_sensitivity))
    var camera_rotation_limit = deg2rad(90)
    camera.rotation_x = clamp(camera.rotation_x, -camera_rotation_limit, camera_rotation_limit)

    # Handle movement
    var direction = Vector3()
    if Input.is_action_pressed("ui_up"):
        direction -= transform.basis.z
    if Input.is_action_pressed("ui_down"):
        direction += transform.basis.z
    if Input.is_action_pressed("ui_left"):
        direction -= transform.basis.x
    if Input.is_action_pressed("ui_right"):
        direction += transform.basis.x

    direction = direction.normalized() * speed
    velocity.y -= gravity * delta

    # Move and slide
    velocity = move_and_slide(direction, Vector3.UP)

func _input(event):
    if event is InputEventMouseMotion:
        rotate_y(deg2rad(-event.relative.x * mouse_sensitivity))
