void countWordOccurrences(const std::string& filename, const std::string& word) {
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Error: Could not open file " << filename << std::endl;
        return;
    }

    std::unordered_map<std::string, int> wordCount;
    std::string line;

    while (std::getline(file, line)) {
        size_t pos = 0;
        while ((pos = line.find(word, pos)) != std::string::npos) {
            wordCount[word]++;
            pos += word.length(); // Move past the word to avoid multiple counts
        }
    }

    file.close();

    std::cout << "Word '" << word << "' occurred " << wordCount[word] << " times in the file." << std::endl;
}

