#include <iostream>
#include <fstream>
#include <string>
#include <unordered_map>

// Function to count word occurrences in a file
int countWordOccurrences(const std::string& filename, const std::string& word) {
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Error opening file: " << filename << std::endl;
        return -1; // Or handle error differently
    }

    std::unordered_map<std::string, int> wordCount;
    std::string line, currentWord;

    // Convert word to lowercase for case-insensitive matching (optional)
    std::string targetWord = word;
    std::transform(targetWord.begin(), targetWord.end(), targetWord.begin(), ::tolower);

    while (std::getline(file, line)) {
        std::istringstream lineStream(line);
        while (lineStream >> currentWord) {
            std::string lowerWord = currentWord;
            std::transform(lowerWord.begin(), lowerWord.end(), lowerWord.begin(), ::tolower);
            if (lowerWord == targetWord) {
                wordCount[currentWord]++;
            }
        }
    }

    // Return the count of the specific word (or sum if case-insensitive)
    if (wordCount.find(targetWord) != wordCount.end()) {
        return wordCount.at(targetWord); // For exact match, or sum wordCount values if all cases combined
    }

    // In case the word wasn't found (optional specific check)
    std::cout << "Word '" << word << "' not found." << std::endl;
    return 0; // Default return when not case-insensitive or exact word not found
}

