#include <vector>

int count_word_occurrences(const std::vector<std::string>& lines, const std::string& word) {
    int count = 0;
    for (const std::string& line : lines) {
        size_t pos = 0;
        while ((pos = line.find(word, pos)) != std::string::npos) {
            count++;
            pos += word.length();
        }
    }
    return count;
}

int main() {
    std::string filename, word, line;

    std::cout << "Enter the name of the text file: ";
    std::cin >> filename;

    std::cout << "Enter the word to count occurrences of: ";
    std::cin >> word;

    std::ifstream input_file(filename);
    if (!input_file.is_open()) {
        std::cerr << "Error opening the file." << std::endl;
        return 1;
    }

    std::vector<std::string> lines;
    while (std::getline(input_file, line)) {
        lines.push_back(line);
    }
    input_file.close();

    int occurrences = count_word_occurrences(lines, word);
    std::cout << "The word '" << word << "' occurs " << occurrences << " times in the file." << std::endl;

    return 0;
}
