            return interaction.reply({ content: 'I do not have the necessary permissions to ban members.', ephemeral: true });
        }

        // Ban the member
        try {
            await interaction.guild.members.ban(member, { reason });
            return interaction.reply({ content: `Member ${member.tag} has been banned for ${reason}.`, ephemeral: false });
        } catch (error) {
            return interaction.reply({ content: `Failed to ban member ${member.tag}.`, ephemeral: true });
        }
    }
