#include <SDL2/SDL.h>
#include <SDL2/SDL_image.h>
#include <stdio.h>
#include <stdlib.h>

// Define constants
#define SCREEN_WIDTH 800
#define SCREEN_HEIGHT 600
#define BLOCK_SIZE 20

// Define colors
#define WHITE SDL_Color { 255, 255, 255 }
#define BLACK SDL_Color { 0, 0, 0 }

// Define game structures
typedef struct {
    int x, y;
    int width, height;
} Rectangle;

typedef struct {
    int x, y;
    int width, height;
} Snake;

typedef struct {
    int x, y;
    int width, height;
} Food;

// Function to initialize SDL
void initSDL() {
    if (SDL_Init(SDL_INIT_VIDEO) != 0) {
        printf("SDL initialization failed: %s\n", SDL_GetError());
        exit(1);
    }
}

// Function to load image
SDL_Texture* load_image(const char* filename) {
    SDL_Surface* surface = IMG_Load(filename);
    if (!surface) {
        printf("Failed to load image: %s\n", SDL_GetError());
        exit(1);
    }
    SDL_Texture* texture = SDL_CreateTextureFromSurface(SDL_GetRenderer(SDL_GetWindowFromID(SDL_GetWindowID(SDL_CreateWindow("Snake Game", 100, 100, SCREEN_WIDTH, SCREEN_HEIGHT, SDL_WINDOW_SHOWN))), surface);
    SDL_FreeSurface(surface);
    return texture;
}

// Function to draw rectangle
void draw_rectangle(SDL_Renderer* renderer, SDL_Texture* texture, int x, int y, int width, int height) {
    SDL_Rect rect = { x, y, width, height };
    SDL_RenderCopy(renderer, texture, NULL, &rect);
}

// Function to draw snake
void draw_snake(SDL_Renderer* renderer, SDL_Texture* texture, Snake snake) {
    for (int i = 0; i < snake.width; i++) {
        draw_rectangle(renderer, texture, snake.x + i * BLOCK_SIZE, snake.y, BLOCK_SIZE, BLOCK_SIZE);
    }
}

// Function to draw food
void draw_food(SDL_Renderer* renderer, SDL_Texture* texture, Food food) {
    draw_rectangle(renderer, texture, food.x, food.y, food.width, food.height);
}

// Function to handle events
void handle_events(SDL_Event* event, Snake* snake, Food* food) {
    if (event->type == SDL_QUIT) {
        SDL_Quit();
    } else if (event->type == SDL_KEYDOWN) {
        switch (event->key.keysym.sym) {
            case SDLK_UP:
                snake->y -= BLOCK_SIZE;
                break;
            case SDLK_DOWN:
                snake->y += BLOCK_SIZE;
                break;
            case SDLK_LEFT:
                snake->x -= BLOCK_SIZE;
                break;
            case SDLK_RIGHT:
                snake->x += BLOCK_SIZE;
                break;
        }
    }
}

// Function to update game state
void update_game_state(Snake* snake, Food* food) {
    // Update snake position
    snake->x += BLOCK_SIZE;
    snake->y += BLOCK_SIZE;

    // Check collision with food
    if (snake->x == food->x && snake->y == food->y) {
        // Increase snake length
        snake->width++;
    }
}

// Function to render game
void render_game(SDL_Renderer* renderer, SDL_Texture* snake_texture, SDL_Texture* food_texture, Snake snake, Food food) {
    SDL_SetRenderDrawColor(renderer, WHITE.r, WHITE.g, WHITE.b, WHITE.a);
    SDL_RenderClear(renderer);

    draw_snake(renderer, snake_texture, snake);
    draw_food(renderer, food_texture, food);

    SDL_RenderPresent(renderer);
}

// Function to handle game loop
void game_loop(SDL_Window* window, SDL_Renderer* renderer, SDL_Texture* snake_texture, SDL_Texture* food_texture, Snake* snake, Food* food) {
    SDL_Event event;
    while (1) {
        while (SDL_PollEvent(&event)) {
            handle_events(&event, snake, food);
        }

        update_game_state(snake, food);

        render_game(renderer, snake_texture, food_texture, *snake, *food);

        SDL_Delay(1000 / 60);
    }
}

int main() {
    // Initialize SDL
    initSDL();

    // Create window
    SDL_Window* window = SDL_CreateWindow("Snake Game", 100, 100, SCREEN_WIDTH, SCREEN_HEIGHT, SDL_WINDOW_SHOWN);

    // Create renderer
    SDL_Renderer* renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);

    // Load images
    SDL_Texture* snake_texture = load_image("snake.png");
    SDL_Texture* food_texture = load_image("food.png");

    // Initialize game state
    Snake snake = { 0, 0, 1, 1 };
    Food food = { 100, 100, 20, 20 };

    // Start game loop
    game_loop(window, renderer, snake_texture, food_texture, &snake, &food);

    // Clean up
    SDL_DestroyTexture(snake_texture);
    SDL_DestroyTexture(food_texture);
    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    SDL_Quit();

    return 0;
}
