#include <fstream>
#include <sstream>
#include <string>
#include <map>

// Function to count word occurrences
int countWord(const std::string& word, const std::string& filename) {
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Error: Unable to open file." << std::endl;
        return -1;
    }

    std::map<std::string, int> wordCount;
    std::string line, wordCopy;

    while (std::getline(file, line)) {
        std::istringstream iss(line);
        while (iss >> wordCopy) {
            // Convert to lowercase for case-insensitive comparison
            for (char& c : wordCopy) {
                c = std::tolower(c);
            }

            if (wordCopy == word) {
                ++wordCount[word];
            }
        }
    }

    file.close();
    return wordCount[word];
}

int main() {
    std::string filename, word;
    std::cout << "Enter the filename: ";
    std::cin >> filename;
    std::cout << "Enter the word to count: ";
    std::cin >> word;

    int count = countWord(word, filename);
    if (count != -1) {
        std::cout << "The word '" << word << "' appears " << count << " times in the file." << std::endl;
