public boolean isPowerOfThree(int n) {
    // Check if n is a positive integer
    if (n <= 0) {
        return false;
    }

    // Check if n is a multiple of 3
    if (n % 3 != 0) {
        return false;
    }

    // Check if n is a multiple of 3^x
    while (n % 3 == 0) {
        n /= 3;
    }

    return n == 1;
}
