#include <iostream>
#include <algorithm>

void findCommonElements(int arr1[], int n1, int arr2[], int n2) {
    std::sort(arr1, arr1 + n1);
    std::sort(arr2, arr2 + n2);

    int i = 0, j = 0;
    while (i < n1 && j < n2) {
        if (arr1[i] == arr2[j]) {
            std::cout << arr1[i] << " ";
            i++;
            j++;
        } else if (arr1[i] < arr2[j]) {
            i++;
        } else {
            j++;
        }
    }
}

