    int arr1[] = {1, 2, 3, 4, 5};
    int n1 = sizeof(arr1) / sizeof(arr1[0]);
    int arr2[] = {3, 4, 5, 6, 7};
    int n2 = sizeof(arr2) / sizeof(arr2[0]);

    findCommonElements(arr1, n1, arr2, n2);

    return 0;
}
