#include <string>
#include <iostream>
#include <regex>

std::string convertMarkdownToHtml(std::string& markdown) {
    std::string html;
    std::regex markdown_regex(R"(\[(.*?)\]\((.*?)\))");
    std::smatch match;
    while (std::regex_search(markdown, match, markdown_regex)) {
        std::string link = match[2].str();
        std::string text = match[1].str();
        html += "<a href=\"" + link + "\">" + text + "</a>";
        markdown = match.suffix();
    }
    return html;
}
