```javascript
const { Client, Intents, Collection } = require('discord.js');
const { REST } = require('@discordjs/rest');
const { Routes } = require('discord-api-structures');
const { clientId, guildId, token } = require('./config.json');

const client = new Client({ intents: [Intents.FLAGS.GUILDS] });

client.commands = new Collection();

const commands = [
  {
    name: 'ban',
    description: 'Bans a user from the server.',
    options: [
      {
        name: 'user',
        description: 'The user to ban.',
        type: 'USER',
        required: true,
      },
      {
        name: 'reason',
        description: 'The reason for the ban.',
        type: 'STRING',
        required: false,
      },
    ],
  },
];

const rest = new REST({ version: '10' }).setToken(token);

(async () => {
  try {
    console.log('Started refreshing application (/) commands.');

    await rest.put(Routes.applicationCommands(clientId), { body: commands });

    console.log('Successfully reloaded application (/) commands.');
  } catch (error) {
    console.error(error);
  }
})();

client.on('interactionCreate', async interaction => {
  if (!interaction.isCommand()) return;

  const command = client.commands.get(interaction.commandName);

  if (!command) return;

  try {
    await command.execute(interaction);
  } catch (error) {
    console.error(error);
    await interaction.reply({ content: 'There was an error while executing this command!', ephemeral: true });
  }
});

async function ban(interaction) {
  const user = interaction.options.getUser('user');
  const reason = interaction.options.getString('reason') || 'No reason provided';

  try {
    await interaction.guild.members.ban(user, { reason });
    await interaction.reply({ content: `Banned ${user.tag} for ${reason}`, ephemeral: true });
  } catch (error) {
    await interaction.reply({ content: 'There was an error while banning the user.', ephemeral: true });
  }
}

client.commands.set('ban', ban);

client.login(token);
```

