std::string convert_markdown_to_html(const std::string& markdown) {
    std::string html;
    std::regex regexes[] = {
        std::regex("\\#(\\w+)"),
        std::regex("\\*(\\w+)*"),
        std::regex("\\`(\\w+)`"),
        std::regex("\\[(\\w+)\\]\\((\\w+)\\)")
    };

    std::smatch match;

    for (const auto& regex : regexes) {
        while (std::regex_search(markdown, match, regex)) {
            std::string replacement;
            switch (regex.id()) {
                case 1:
                    replacement = "<h1>" + match[1] + "</h1>";
                    break;
                case 2:
                    replacement = "<strong>" + match[1] + "</strong>";
                    break;
                case 3:
                    replacement = "<code>" + match[1] + "</code>";
                    break;
                case 4:
                    replacement = "<a href=\"" + match[2] + "\">" + match[1] + "</a>";
                    break;
            }
            markdown = std::regex_replace(markdown, match, replacement);
        }
    }

    return html;
}

int main() {
    std::string markdown = "# Hello World\n\n* This is a list\n\n`This is code`";
    std::string html = convert_markdown_to_html(markdown);
    std::cout << html << std::endl;
    return 0;
}

