#include <SDL2/SDL.h>
#include <SDL2/SDL_image.h>
#include <stdio.h>
#include <stdlib.h>

#define SCREEN_WIDTH 640
#define SCREEN_HEIGHT 480

// Define the snake struct
struct Snake {
    int x;
    int y;
    int length;
    SDL_Rect body[100];
};

// Function prototypes
void init_snake(struct Snake *snake);
void move_snake(struct Snake *snake, int direction);
void draw_snake(struct Snake *snake, SDL_Renderer *renderer);
void check_collisions(struct Snake *snake);
void check_food(struct Snake *snake, SDL_Rect *food);
void handle_events(SDL_Event *event);

int main(int argc, char *argv[]) {
    // Initialize SDL
    SDL_Init(SDL_INIT_VIDEO);
    SDL_Window *window = SDL_CreateWindow("Snake", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, SCREEN_WIDTH, SCREEN_HEIGHT, SDL_WINDOW_SHOWN);
    SDL_Renderer *renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);

    // Initialize the snake
    struct Snake snake;
    init_snake(&snake);

    // Set the initial direction of the snake
    int direction = SDL_SCANCODE_UP;

    // Create the food
    SDL_Rect food;
    food.x = rand() % SCREEN_WIDTH;
    food.y = rand() % SCREEN_HEIGHT;
    food.w = 10;
    food.h = 10;

    // Main game loop
    bool running = true;
    while (running) {
        // Handle events
        SDL_Event event;
        while (SDL_PollEvent(&event)) {
            handle_events(&event);
        }

        // Move the snake
        move_snake(&snake, direction);

        // Check for collisions
        check_collisions(&snake);

        // Check for food
        check_food(&snake, &food);

        // Draw the game
        SDL_SetRenderDrawColor(renderer, 0, 0, 0, 255);
        SDL_RenderClear(renderer);
        draw_snake(&snake, renderer);
        SDL_RenderPresent(renderer);
    }

    // Clean up
    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    SDL_Quit();

    return 0;
}

void init_snake(struct Snake *snake) {
    snake->x = SCREEN_WIDTH / 2;
    snake->y = SCREEN_HEIGHT / 2;
    snake->length = 3;
    for (int i = 0; i < snake->length; i++) {
        snake->body[i].x = snake->x - i;
        snake->body[i].y = snake->y;
        snake->body[i].w = 10;
        snake->body[i].h = 10;
    }
}

void move_snake(struct Snake *snake, int direction) {
    // Move the head of the snake
    switch (direction) {
        case SDL_SCANCODE_UP:
            snake->y--;
            break;
        case SDL_SCANCODE_DOWN:
            snake->y++;
            break;
        case SDL_SCANCODE_LEFT:
            snake->x--;
            break;
        case SDL_SCANCODE_RIGHT:
            snake->x++;
            break;
    }

    // Move the body of the snake
    for (int i = snake->length - 1; i > 0; i--) {
        snake->body[i].x = snake->body[i - 1].x;
        snake->body[i].y = snake->body[i - 1].y;
    }

    // Move the tail of the snake
    snake->body[0].x = snake->x;
    snake->body[0].y = snake->y;
}

void draw_snake(struct Snake *snake, SDL_Renderer *renderer) {
    for (int i = 0; i < snake->length; i++) {
        SDL_Rect rect = snake->body[i];
        SDL_SetRenderDrawColor(renderer, 0, 255, 0, 255);
        SDL_RenderFillRect(renderer, &rect);
    }
}

void check_collisions(struct Snake *snake) {
    // Check for collisions with the walls
    if (snake->x < 0 || snake->x >= SCREEN_WIDTH || snake->y < 0 || snake->y >= SCREEN_HEIGHT) {
        running = false;
    }

    // Check for collisions with the snake's body
    for (int i = 1; i < snake->length; i++) {
        if (snake->body[0].x == snake->body[i].x && snake->body[0].y == snake->body[i].y) {
            running = false;
        }
    }
}

void check_food(struct Snake *snake, SDL_Rect *food) {
    if (snake->body[0].x == food->x && snake->body[0].y == food->y) {
        snake->length++;
        food->x = rand() % SCREEN_WIDTH;
        food->y = rand() % SCREEN_HEIGHT;
    }
}

void handle_events(SDL_Event *event) {
    switch (event->type) {
        case SDL_KEYDOWN:
            switch (event->key.keysym.sym) {
                case SDL_SCANCODE_UP:
                    direction = SDL_SCANCODE_UP;
                    break;
                case SDL_SCANCODE_DOWN:
                    direction = SDL_SCANCODE_DOWN;
                    break;
                case SDL_SCANCODE_LEFT:
                    direction = SDL_SCANCODE_LEFT;
                    break;
                case SDL_SCANCODE_RIGHT:
                    direction = SDL_SCANCODE_RIGHT;
                    break;
            }
            break;
    }
}

