   const { Client, GatewayIntentBits, SlashCommandBuilder, Events } = require('discord.js');
   const client = new Client({ intents: [GatewayIntentBits.Guilds, GatewayIntentBits.GuildMessages] });

   // Create a command builder for the ban command
   const banCommand = new SlashCommandBuilder()
     .setName('ban')
     .setDescription('Bans a user from the server')
     .addUserOption(option => option.setName('user').setDescription('The user to ban').setRequired(true))
     .addStringOption(option => option.setName('reason').setDescription('The reason for the ban').setRequired(false));

   // Register the command
   client.on(Events.InteractionCreate, async interaction => {
     if (!interaction.isChatInputCommand()) return;

     const command = interaction.commandName;
     if (command === 'ban') {
       const user = interaction.options.getUser('user');
       const reason = interaction.options.getString('reason') || 'No reason provided';

       // Check if the user has the necessary permissions
       if (!interaction.member.permissions.has('BAN_MEMBERS')) {
         return interaction.reply({ content: 'You do not have permission to ban members.', ephemeral: true });
       }

       // Check if the user is the bot
       if (user.id === client.user.id) {
         return interaction.reply({ content: 'You cannot ban the bot.', ephemeral: true });
       }

       // Ban the user
       try {
         await interaction.guild.members.ban(user, { days: 7, reason: reason });
         return interaction.reply({ content: `Banned ${user.tag} for ${reason}.`, ephemeral: true });
       } catch (error) {
         return interaction.reply({ content: `Failed to ban ${user.tag}.`, ephemeral: true });
       }
     }
   });

