```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <map>

int main() {
    std::string wordToCount;  // The word to count
    std::string line;         // A line from the file
    std::map<std::string, int> wordCount;  // Map to store word counts

    // Prompt user to enter the word to count
    std::cout << "Enter the word to count: ";
    std::cin >> wordToCount;

    // Open the file for reading
    std::ifstream inputFile("path/to/your/file.txt");

    // Check if the file was opened successfully
    if (inputFile.is_open()) {
        // Read the file line by line
        while (std::getline(inputFile, line)) {
            // Split the line into words
            std::istringstream iss(line);
            std::string word;
            while (iss >> word) {
                // Check if the word matches the word to count
                if (word == wordToCount) {
                    // Increment the count for the word
                    ++wordCount[word];
                }
            }
        }
        inputFile.close();
    } else {
        std::cerr << "Error opening file" << std::endl;
        return 1;
    }

    // Print the word count
    std::cout << "The word '" << wordToCount << "' appears " << wordCount[wordToCount] << " times in the file." << std::endl;

    return 0;
}
